mapper.select:
select
distinct
r.* from MOT.T_DCS_RESOURCE r
r.PARENT_ID is null and
r.PARENT_ID = #{parentId}
where
and
(id in (select userresour5_.RESOURCE_ID from MOT.T_DCS_USER_RESOURCE_RELATION userresour5_ where userresour5_.RESOURCE_ID = r.id and userresour5_.AUTHORIZE_TYPE = #{authorizeType} and userresour5_.USER_ID = #{userId}) or id in (select roleresour1_.RESOURCE_ID from MOT.T_DCS_ROLE_RESOURCE_RELATION roleresour1_ where roleresour1_.RESOURCE_ID = r.id and roleresour1_.AUTHORIZE_TYPE =#{authorizeType} and roleresour1_.role_id in (select roleuserre3_.role_id from MOT.T_DCS_USER_ROLE_RELATION roleuserre3_ where roleuserre3_.AUTHORIZE_TYPE = #{authorizeType} and roleuserre3_.USER_ID = #{userId}))
)
and r.RESOURCE_TYPE = #{resourceType}
and r.PARENT_ID = #{parentId}
order by r.ORDER_NUM asc
